function [Ke,ce,fe]=flw2(ex,ey,D,rho,cp,Q,t)  ir=2; ngp=ir*ir; %We use 4 integration points/element  g1=0.577350269189626; w1=1;  gp(:,1)=[-g1; g1;-g1; g1];  gp(:,2)=[-g1;-g1; g1; g1];  w(:,1)=[ w1; w1; w1; w1];   w(:,2)=[ w1; w1; w1; w1];  wp=w(:,1).*w(:,2);  xsi=gp(:,1);  eta=gp(:,2);  r2=ngp*2;  N(:,1)=(1-xsi).*(1-eta)/4;  N(:,2)=(1+xsi).*(1-eta)/4;  N(:,3)=(1+xsi).*(1+eta)/4;  N(:,4)=(1-xsi).*(1+eta)/4;  dNr(1:2:r2,1)=-(1-eta)/4;     dNr(1:2:r2,2)= (1-eta)/4;  dNr(1:2:r2,3)= (1+eta)/4;     dNr(1:2:r2,4)=-(1+eta)/4;  dNr(2:2:r2+1,1)=-(1-xsi)/4;   dNr(2:2:r2+1,2)=-(1+xsi)/4;  dNr(2:2:r2+1,3)= (1+xsi)/4;   dNr(2:2:r2+1,4)= (1-xsi)/4;  fe=zeros(4,1); Ke=zeros(4,4); ce=zeros(4,4);  JT=dNr*[ex;ey]';   for i=1:ngp    indx=[ 2*i-1; 2*i ];    detJ=det(JT(indx,:));    if detJ<10*eps      disp('Jacobideterminanten lika med noll!')    end    JTinv=inv(JT(indx,:));    B=JTinv*dNr(indx,:);    Ke=Ke+B'*D*B*detJ*wp(i)*t;        ce(i,i)=rho*cp*detJ*wp(i);    fe=fe+N(i,:)'*detJ*wp(i)*t*Q;  end%--------------------------end--------------------------------